import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:shop_app/moduels/search/search_screen.dart';import 'package:shop_app/moduels/shop_login_screen/shop_login_screen.dart';import 'package:shop_app/shared/cubit/cubit.dart';import 'package:shop_app/shared/cubit/state.dart';import 'package:shop_app/shared/network/local/shared_prefercne.dart';class ShopAppLayoutScreen extends StatelessWidget {  const ShopAppLayoutScreen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return BlocProvider(      create: (context) => ShopCubit()        ..getHomeData()        ..getCategoriesData()        ..getFavorites()        ..getUserData(),      child: BlocConsumer<ShopCubit, ShopStates>(        listener: (context, state) {},        builder: (context, state) {          var cubit = ShopCubit.get(context);          return Scaffold(            appBar: AppBar(              title: Text(                'Salla',              ),              actions: [                IconButton(                  onPressed: () {                    Navigator.push(                      context,                      MaterialPageRoute(                        builder: (context) => SearchScreen(),                      ),                    );                  },                  icon: Icon(                    Icons.search,                    color: Colors.black,                  ),                ),              ],            ),            bottomNavigationBar: BottomNavigationBar(              currentIndex: cubit.currentIndex,              onTap: (index) {                cubit.changeBottomNavBar(index);              },              items: [                BottomNavigationBarItem(                  icon: Icon(                    Icons.home,                  ),                  label: 'Home',                ),                BottomNavigationBarItem(                  icon: Icon(                    Icons.apps_outlined,                  ),                  label: 'Categories',                ),                BottomNavigationBarItem(                  icon: Icon(                    Icons.favorite,                  ),                  label: 'Favorites',                ),                BottomNavigationBarItem(                  icon: Icon(                    Icons.settings,                  ),                  label: 'Setting',                ),              ],            ),            body: cubit.screens[cubit.currentIndex],          );        },      ),    );  }}// TextButton(// onPressed: () {// CasheHelper.removeData(key: 'token').then(// (value) {// Navigator.pushAndRemoveUntil(// context,// MaterialPageRoute(// builder: (context) => ShopLoginScreen(),// ),// (route) => false,// );// },// );// },// child: Text(// 'bello',// ),// ),