import 'package:shared_preferences/shared_preferences.dart';class CasheHelper {  static SharedPreferences? sharedPreferences;  static init() async {    sharedPreferences = await SharedPreferences.getInstance();  }  // put data  static Future<bool> putData({    required String key,    required dynamic value,  }) async {    if (value is bool) {      return await sharedPreferences!.setBool(key, value);    } else if (value is String) {      return await sharedPreferences!.setString(key, value);    } else {      return await sharedPreferences!.setInt(key, value);    }  }  //  get data  static dynamic getData({    required String key,  }) {    return sharedPreferences!.get(key);  }  // remove  data  static Future<bool> removeData({    required String key,  }) async {    return await sharedPreferences!.remove(key);  }}